$(document).ready(function(){setTimeout(function(){var e=new Date;var t=new Date;var n=new Date;var r=n.getHours()*3600+n.getMinutes()*60+n.getSeconds();var i=24*3600-r;e.setTime(n.getTime()+i*1e3);t.setTime(n.getTime()+ilyio_interval*60*1e3);if($.cookie("ilyio_flag")==undefined){$.cookie("ilyio_flag",true,{expires:t});if($.cookie("ilyio_num_times")==undefined){$.cookie("ilyio_num_times",0,{expires:e});window.location="http://localhost:3000/"+ilyio_user_id+"+/"+window.location.href}else if($.cookie("ilyio_num_times")!=ilyio_maximum){var s=parseInt($.cookie("ilyio_num_times"))+1;$.cookie("ilyio_num_times",s,{expires:e});window.location="http://localhost:3000/"+ilyio_user_id+"+/"+window.location.href}else{$.removeCookie("ilyio_flag")}}},parseInt(ilyio_delay))})